import {useState, Component} from "react";

class App extends Component{

  constructor(props){
    super(props);
    console.log("Aquí entra constructor")
    this.state = {
      name: "",
    };
  }

  componentDidMount(){
    console.log("Aquí entra DidMount");
  }

  componentDidUpdate(){
    console.log("Aquí entra DidUpdate");
  }

  // mostrar hasta aquí para enfatizar que DidUpdate no se ve en console porque no
  // hay alguna actualización de estado

  render(){
    return(
      <div>
        <h1>
          Hola
        </h1>
        <input value={this.state.name}></input>
      </div>
    );
  }

}

export default App;
